// JavaScript 逻辑保持不变
export default {
  name: 'KnowledgeEditModal',
  // 注意：组件名是 KnowledgeEditModal，但原代码注释是 KnowledgeCreateModal
  props: {
    visible: {
      type: Boolean,
      required: true
    },
    dataInfo: {
      type: Object,
      default: () => ({})
    }
  },
  data() {
    return {
      form: {
        name: '',
        description: ''
      },
      rules: {
        name: [{
          required: true,
          message: '请输入知识库名称',
          trigger: 'blur'
        }],
        description: [{
          max: 500,
          message: '最多输入500字',
          trigger: 'blur'
        }]
      },
      activeNames: []
    };
  },
  methods: {
    handleSubmit() {
      this.$refs.form.validate(valid => {
        if (valid) {
          // 提交 dataInfo 的数据
          this.$emit('submit', {
            ...this.dataInfo
          });
        }
      });
    },
    handleClose() {
      this.$emit('update:visible', false);
      // 由于 model 是 props 的 dataInfo，这里不需要 resetFields
      // 如果需要重置，建议在父组件中传递 dataInfo 的副本
      // this.$refs.form.resetFields() 
    }
  }
};
