import service from '@/utils/request'

/**
 * 获取文件列表相关接口
 */
export const getFileListByPath = (param) => {
  return service({
    method: 'get',
    url: '/api/file/getfilelist',
    params: param // ✅ 正确带上参数
  })
}

/**
 * 删除文件
 * @returns 
 */
export const deleteFile = () => {
    return service({
        method: 'get',
        url: '/file/search'
    })
}

export const getFileListByType = () => {
    return service({
        method: 'get',
        url: '/file/selectfilebyfiletype'
    })
}
export const getRecoveryFile = () => {
    return service({
        method: 'get',
        url: '/recoveryfile/list'
    })
}
export const getMyShareFileList = () => {
    return service({
        method: 'get',
        url: '/share/shareList'
    })
}
export const searchFile = () => {
    return service({
        method: 'get',
        url: '/file/search'
    })
}
