export default {
  name: 'KnowledgeCreateModal',
  props: {
    visible: {
      type: Boolean,
      required: true
    }
  },
  data() {
    return {
      form: {
        name: '',
        description: ''
      },
      rules: {
        name: [{
          required: true,
          message: '请输入知识库名称',
          trigger: 'blur'
        }],
        description: [{
          max: 500,
          message: '最多输入500字',
          trigger: 'blur'
        }]
      },
      activeNames: []
    };
  },
  methods: {
    handleSubmit() {
      this.$refs.form.validate(valid => {
        if (valid) {
          this.$emit('submit', {
            ...this.form
          });
          // this.handleClose()
        }
      });
    },
    handleClose() {
      this.$emit('update:visible', false);
      this.$refs.form.resetFields();
    }
  }
};
