import { getFileListByPath } from '@/api/knowledge';
import KnowledgeCard from './KnowledgeCard.vue';
export default {
  components: {
    KnowledgeCard
  },
  data() {
    return {
      pageData: {
        current: 1,
        size: 10
      },
      cardList: [] // 知识库列表
    };
  },
  mounted() {
    this.getList();
  },
  methods: {
    getList() {
      const that = this;
      let param = {
        ...this.pageData
      };
      getFileListByPath(param).then(res => {
        if (res.data.code === 200) {
          this.cardList = res.data.data.records;
        } else {
          this.cardList = [];
          that.$message.error(res.data.message);
        }
      });
    }
  }
};
