import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import FileList from '@/components/file/FileList.vue';
import BreadCrumb from '@/components/file/components/BreadCrumb.vue';
import FileTable from '@/components/common/FileTable.vue';
import OperationMenu from '@/components/file/OperationMenu.vue';
import FileGrid from '@/components/file/components/FileGrid.vue';
import FileTimeLine from '@/components/file/components/FileTimeLine.vue';
import { getFileListByPath, getFileListByType, getRecoveryFile, getMyShareFileList, searchFile } from '@/api/file.js';
export default {
  name: "Pan",
  // components: { FileList,},
  components: {
    FileTable,
    OperationMenu,
    FileGrid,
    BreadCrumb,
    FileTimeLine
  },
  data() {
    return {
      isRefreshData: "",
      fileNameSearch: '',
      loading: true,
      //  表格数据-loading
      fileList: [],
      //  表格数据-文件列表
      // 分页数据
      pageData: {
        currentPage: 1,
        pageCount: 50,
        total: 0
      }
    };
  },
  created() {
    this.setPageCount();
    //lml
    this.getTableDataByType();
  },
  watch: {
    //lml
    filePath() {
      // 当左侧菜单选择“全部”或“我的分享”，文件路径发生变化时，再重新获取文件列表
      if (this.$route.name === 'File' && [0, 8].includes(this.fileType)) {
        this.setPageCount();
        this.getTableDataByType();
      }
    },
    //lml
    fileType() {
      if (this.$route.name === 'File') {
        this.setPageCount();
        this.getTableDataByType();
      }
    },
    // 监听文件查看模式
    fileModel() {
      this.setPageCount();
    },
    refreshData(newVal) {
      // 每当 message 变化，执行逻辑
      console.log('父组件传值变化:', newVal);
      this.showFileList();
    }
  },
  computed: {
    // 左侧菜单选中的文件类型
    fileType() {
      return this.$route.query.fileType ? Number(this.$route.query.fileType) : 0;
    },
    // 当前所在路径
    filePath() {
      return this.$route.query.filePath ? this.$route.query.filePath : '/';
    },
    // 文件查看模式 0列表模式 1网格模式 2 时间线模式
    fileModel() {
      return this.$store.getters.fileModel;
    },
    // 屏幕宽度
    screenWidth() {
      return this.$store.state.common.screenWidth;
    }
  },
  methods: {
    handleSelect(key) {
      const fileType = Number(key);
      this.$router.push({
        name: this.$route.name,
        query: {
          ...this.$route.query,
          fileType: fileType,
          filePath: '/' // 可选：重置路径，也可以保留当前 filePath
        }
      });
    },
    /**
    * 文件展示区域的空白处右键事件
    * @param {Document} event 右键事件对象
    * //lml
    */
    handleClickRight(event) {
      console.log('文件展示区域的空白处右键事件:');
      event.preventDefault();
      // 只有在全部页面才可以进行以下操作
      if (![6, 8].includes(this.fileType)) {
        this.$openBox.contextMenu({
          selectedFile: undefined,
          domEvent: event,
          serviceEl: this
        }).then(res => {
          if (res === 'confirm') {
            this.getTableDataByType(); //  刷新文件列表
            // this.$store.dispatch('showStorage') //  刷新存储容量
          }
        });
      }
    },
    /**
     * 表格数据获取相关事件 | 调整分页大小
     */
    setPageCount() {
      this.pageData.currentPage = 1;
      if (this.fileModel === 0) {
        this.pageData.pageCount = 50;
      }
      if (this.fileModel === 1) {
        this.pageData.pageCount = 100;
      }
    },
    /**
     * 表格数据获取相关事件 | 获取文件列表数据
     * lml
     */
    getTableDataByType() {
      this.loading = true;
      console.log('getTableDataByType===');
      // 分类型
      if (Number(this.fileType)) {
        switch (Number(this.fileType)) {
          case 6:
            {
              this.showFileRecovery(); //  回收站
              break;
            }
          case 8:
            {
              this.showMyShareFile(); //  我的分享
              break;
            }
          default:
            {
              this.showFileList();
              break;
            }
        }
      } else {
        // 全部文件
        this.showFileList();
      }
      // this.$store.dispatch('showStorage')
    },
    /**
     * 表格数据获取相关事件 | 获取当前路径下的文件列表
     */
    showFileList() {
      console.log('showFileList==');
      let data = {
        fileType: this.fileType,
        filePath: this.filePath,
        currentPage: this.pageData.currentPage,
        pageCount: this.pageData.pageCount
      };
      if (this.refreshData) {
        data.fileType = this.refreshData;
      }
      getFileListByPath(data).then(res => {
        if (res && res.data && res.data.code === 200) {
          this.fileList = res.data.data.records;
          this.pageData.total = Number(res.data.data.total);
          this.loading = false;
        } else {
          this.$message.error(res.data.message);
        }
      });
    },
    /**
     * 表格数据获取相关事件 | 分页组件 | 当前页码改变
     * 
     * //lml
     */
    handleCurrentChange(currentPage) {
      this.pageData.currentPage = currentPage;
      this.getTableDataByType();
    },
    /**
     * 表格数据获取相关事件 | 分页组件 | 页大小改变时
     * //lml
     */
    handleSizeChange(pageCount) {
      this.pageData.pageCount = pageCount;
      this.getTableDataByType();
    },
    /**
     * 表格数据获取相关事件 | 获取回收站文件列表
     */
    showFileRecovery() {
      console.log('showFileRecovery==');
      getRecoveryFile().then(res => {
        if (res.success) {
          this.fileList = res.dataList;
          this.loading = false;
        } else {
          this.$message.error(res.message);
        }
      });
    },
    /**
     * 表格数据获取相关事件 | 获取我的分享列表
     */
    showMyShareFile() {
      console.log('showMyShareFile==');
      let data = {
        shareFilePath: this.filePath,
        shareBatchNum: this.$route.query.shareBatchNum,
        currentPage: this.pageData.currentPage,
        pageCount: this.pageData.pageCount
      };
      getMyShareFileList(data).then(res => {
        if (res.success) {
          this.fileList = res.dataList;
          this.pageData.total = Number(res.total);
          this.loading = false;
        } else {
          this.$message.error(res.message);
        }
      });
    },
    /**
     * 表格数据获取相关事件 | 根据文件类型展示文件列表
     */
    showFileListByType() {
      console.log('showFileListByType==');
      //  分类型
      let data = {
        fileType: this.fileType,
        currentPage: this.pageData.currentPage,
        pageCount: this.pageData.pageCount
      };
      if (res.success) {
        this.fileList = res.dataList;
        this.pageData.total = Number(res.total);
        this.loading = false;
      } else {
        this.$message.error(res.message);
      }
    },
    /**
     * 获取搜索文件结果列表
     * @param {string} fileName 文件名称
     */
    getSearchFileList(fileName) {
      this.loading = true;
      searchFile({
        currentPage: this.pageData.currentPage,
        pageCount: this.pageData.pageCount,
        fileName: fileName
      }).then(res => {
        this.loading = false;
        if (res.success) {
          this.fileList = res.dataList.map(item => {
            return {
              ...item,
              highlightFields: item.highLight.fileName[0]
            };
          });
          this.pageData.total = res.data.totalHits;
        } else {
          this.$message.error(res.message);
        }
      });
    }
  }
};
