import { createOfficeFile } from '@/api/onlyoffice.js';
export default {
  name: 'AddFileDialog',
  data() {
    return {
      visible: false,
      //  对话框是否可见
      form: {
        fileName: ''
      },
      formRules: {
        fileName: [{
          required: true,
          message: '请输入文件名称',
          trigger: 'blur'
        }]
      },
      sureBtnLoading: false
    };
  },
  methods: {
    /**
     * 新建文件对话框 | 对话框关闭的回调
     * @description 关闭对话框，重置表单
     */
    handleDialogClose() {
      this.$refs['addFileForm'].resetFields();
      this.visible = false;
      this.callback('cancel');
    },
    /**
     * 新建文件对话框 | 确定按钮点击事件
     * @description 校验表单，校验通过后调用新建文件接口
     * @param {string} formName 表单ref值
     */
    handleDialogSure(formName) {
      this.sureBtnLoading = true;
      this.$refs[formName].validate(valid => {
        if (valid) {
          createOfficeFile({
            extendName: this.extendName,
            filePath: this.filePath,
            fileName: this.form.fileName
          }).then(res => {
            this.sureBtnLoading = false;
            console.log('res============');
            console.log(res);
            if (res.data.code === 200 && res.status) {
              this.$message.success('文件创建成功');
              this.$refs[formName].resetFields();
              this.visible = false;
              this.callback('confirm');
            } else {
              this.$message.warning(res.message);
            }
          }).catch(() => {
            this.sureBtnLoading = false;
          });
        } else {
          this.sureBtnLoading = false;
          return false;
        }
      });
    }
  }
};
