import { getFileListByPath } from '@/api/knowledge';
import KnowledgeCard from './KnowledgeCard.vue';
export default {
  components: {
    KnowledgeCard
  },
  data() {
    return {
      pageData: {
        current: 1,
        size: 10
      },
      cardList: [{
        name: '业务文档库',
        description: '存储和管理所有业务相关文档，包括报告、提案和会议纪要。',
        fileCount: 12,
        updateTimeStr: '2天前',
        bgColor: 'linear-gradient(135deg, #398EFF, #196DFF)',
        icon: 'el-icon-document'
      }, {
        name: '研究资料集',
        description: '收集的学术论文、研究报告和技术文档，用于AI模型训练。',
        fileCount: 28,
        updateTimeStr: '1周前',
        bgColor: 'linear-gradient(135deg, #27C1A9, #14B39B)',
        icon: 'el-icon-s-operation'
      }, {
        name: '创意灵感库',
        description: '设计资源、创意素材和灵感收集，用于内容创作和设计项目。',
        fileCount: 15,
        updateTimeStr: '3天前',
        bgColor: 'linear-gradient(135deg, #9F48F5, #6B2EDA)',
        icon: 'el-icon-lightning'
      }]
    };
  },
  mounted() {
    this.getList();
  },
  methods: {
    getList() {
      let param = {
        ...this.pageData
      };
      getFileListByPath(param).then(res => {
        if (res.data.code === 200) {
          this.cardList = res.data.data.records;
          this.$emit('file-list-loadding', this.cardList[0].knowledgeCode);
        }
      });
    }
  }
};
