import service from '@/utils/request'

/**
 * 查询知识库列表
 */
export const getFileListByPath = (param) => {
  return service({
    method: 'get',
    url: '/api/knowledge/queryKnowledgeList',
    params: param // ✅ 正确带上参数
  })
}

/**
 * 查询知识库文件列表
 */
export const getFileListByType = (param) => {
  return service({
    method: 'get',
    url: '/api/knowledge/queryKnowledgeFileList',
    params: param
  })
}
