export default {
  name: 'SelectColumn',
  data() {
    return {
      dialogVisible: false,
      selectedColumn: [],
      //  被选中的表格需要显示的列
      columnOptions: [{
        value: 'extendName',
        label: '类型'
      }, {
        value: 'fileSize',
        label: '大小'
      }, {
        value: 'uploadTime',
        label: '修改日期'
      }, {
        value: 'deleteTime',
        label: '删除日期'
      }]
    };
  },
  methods: {
    /**
     * 设置显示列按钮点击事件
     * @description 获取 Vuex 中存储的表格显示列
     *              并打开对话框
     */
    handleSetShowColumnBtnClick() {
      this.selectedColumn = this.$store.getters.selectedColumnList;
      this.dialogVisible = true;
    },
    /**
     * 对话框 确定按钮点击事件
     * @description 通过提交 mutation 更新表格显示列
     *              并关闭对话框
     */
    dialogOk() {
      this.$store.commit('changeSelectedColumnList', this.selectedColumn);
      this.dialogVisible = false;
    }
  }
};
